/*global describe:true, it:true, before:true, after:true */
"use strict";

var assert = require('assert'),
    Nemo = require('nemo'),
    nemo = {};
//(new NemoFactory.factory());

describe('@nemo-jaws.constructor@', function () {
    //console.log(process.cwd())
    before(function (done) {
        nemo = Nemo(function (err) {
            if (err !== null) {
                done(err);
                return;
            }
            done();
        });
    });
    after(function (done) {
        nemo.driver.quit();
        done();
    });

    it('should create a jaws function', function (done) {
        assert(nemo && nemo.jaws && nemo.jaws.constructor === Function);

        done();
    });

});

